import React from "react";
import { useState } from "react";

const ContactForm = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    restaurant: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const data = {
      data: {
        type: "contacts",
        attributes: {
          firstName: formData.firstName,
          lastName: formData.lastName,
          email: formData.email,
        },
        relationships: {
          restaurant: {
            data: {
              type: "restaurants",
              id: formData.restaurant,
            },
          },
        },
      },
    };

    fetch("http://localhost:1337/contacts", {
      method: "POST",
      headers: {
        "Content-Type": "application/vnd.api+json",
        Authorization: `Bearer ${process.env.REACT_APP_STRAPI_TOKEN}`,
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success:", data);
        setFormData({
          firstName: "",
          lastName: "",
          email: "",
          restaurant: "",
        });
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        First Name:
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Last Name:
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Restaurant:
        <select name="restaurant" onChange={handleInputChange}>
          <option value="">--Please choose a restaurant--</option>
          <option value="1">Vinland</option>
          <option value="2">Demon Slayer</option>
          <option value="3">Miror</option>
        </select>
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default ContactForm;
