import React, { useState, useEffect } from 'react';

const ContactList = () => {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:1337/api/contacts?populate=*", {
      headers: {
        "Authorization": "Bearer 9934d2bb830e0b27b2b215b3e686079e130490f313bb4b6a81a8439e7e3f7951390238df71b027c040b39052639edf673c0a3a0780ff675ebb11e2fcc7c6678f1ed2b16a7fb4bceae113c0130da7a8929095df847bab17e96044c690392e53d44d10b84594d9c6e0502f733c809aa0c338b6c2870afee28239e43e2d599e115d"
      }
    })
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setContacts(data);
        } else {
          setContacts([]);
        }
      })
      .catch((error) => {
        console.error("Erreur:", error);
        setContacts([]);
      });
  }, []);

  console.log("Contacts:", contacts);

  return (
    <div>
      <h1>Liste des contacts</h1>
      {contacts.length === 0 ? (
        <p>Aucun contact trouv√©.</p>
      ) : (
        <ul>
          {contacts.map((contact) => (
            <li key={contact.id}>
              {contact.attributes.Email}, {contact.attributes.lastName}, {contact.attributes.firstName}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ContactList;
