import React, { useState } from 'react';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    restaurantId: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    fetch('http://localhost:1337/api/contacts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer 9934d2bb830e0b27b2b215b3e686079e130490f313bb4b6a81a8439e7e3f7951390238df71b027c040b39052639edf673c0a3a0780ff675ebb11e2fcc7c6678f1ed2b16a7fb4bceae113c0130da7a8929095df847bab17e96044c690392e53d44d10b84594d9c6e0502f733c809aa0c338b6c2870afee28239e43e2d599e115d',
      },
      body: JSON.stringify({
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        restaurant: formData.restaurantId,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Success:', data);
        setFormData({
          firstName: '',
          lastName: '',
          email: '',
          restaurantId: '',
        });
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        First Name:
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Last Name:
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Restaurant:
        <select name="restaurantId" onChange={handleInputChange}>
          <option value="">--Please choose a restaurant--</option>
          <option value="1">Vinland</option>
          <option value="2">Demon Slayer</option>
          <option value="3">Miror</option>
        </select>
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default ContactForm;
