import React, { useState, useEffect } from "react";

const ContactList = () => {
  const [contacts, setContacts] = useState([]);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");

  const AUTH_TOKEN = "16b88b3820d9381c033aed2778d04e83b94d169ccf3afb9157eefd63812de626401a2da3fe4186a4f712a6b27e53d519ed5ffe30efd354e7ed6f5519dd3c384a383c155b0ecd505b28573c4ad9764ad32ec19593c8c4fb138bda5afa84ea3454b4d1a643a444714d30244b2f5be83b5a3aebab2a5cfe75a2c4bda5547312508c";

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = () => {
    fetch("http://localhost:1337/api/contacts?populate=*", {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${AUTH_TOKEN}`,
      },
    })
      .then((response) => response.json())
      .then((responseData) => {
        const data = responseData.data;
        if (Array.isArray(data)) {
          setContacts(data);
        } else {
          setContacts([]);
        }
      })
      .catch((error) => {
        console.error("Erreur:", error);
        setContacts([]);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  
    const newContact = {
      "attributes": {
        "firstName": firstName,
        "lastName": lastName,
        "Email": email,
        "restaurant": {
          "data": {
            "id": selectedRestaurant,
          },
        },
      },
    };
  
    console.log("Submitting new contact:", newContact);
  
    fetch("http://localhost:1337/api/contacts?populate=*", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${AUTH_TOKEN}`,
      },
      body: JSON.stringify(newContact),
    })
      .then((response) => {
        console.log("API response status:", response.status);
        console.log("API response headers:", response.headers);
        return response.json();
      })
      .then((data) => {
        console.log("API response data:", data);
        fetchContacts();
      })
      .catch((error) => {
        console.error("Erreur:", error);
      });
  };
  
  

  return (
    <div>
      <h1>Liste des contacts</h1>
      {contacts.length === 0 ? (
        <p>Aucun contact trouv√©.</p>
      ) : (
        <ul>
          {contacts.map((contact) => (
            <li key={contact.id}>
              {contact.attributes.Email}, {contact.attributes.lastName}, {contact.attributes.firstName}
            </li>
          ))}
        </ul>
      )}

      <h2>Ajouter un contact</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Pr√©nom :
          <input
            type="text"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </label>
        <label>
          Nom :
          <input
            type="text"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </label>
        <label>
        Email :
        <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        />
        </label>
        <button type="submit">Ajouter</button>
        </form>
        </div>
        );
        };

export default ContactList;
