import React, { useState } from "react";

const ContactForm = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    restaurantId: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    fetch("http://localhost:1337/api/contacts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer 1d3e9a0edf35e1dc8c44fbee58a4ff03643eaa6ce70052facbc83e3539144a1f816ef0e880d1570d0526fa1882a77fbd87a9577feb4d7050fcf7f9887e62d50b16b5b76e6388ec03cd026b5e1d1867c98e16c62f25d5da9c90cfa3487a1cbed48da08dee7d57954e8633cc751e83aad1f4d2fac40538c26abaf5c8132365b5b9`,
      },
      body: JSON.stringify({
        firstName: formData.firstName,
        lastName: formData.lastName,
        Email: formData.email,
        restaurant: formData.restaurantId,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success:", data);
        setFormData({
          firstName: "",
          lastName: "",
          email: "",
          restaurantId: "",
        });
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        First Name:
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Last Name:
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
        />
      </label>
      <br />
      <label>
        Restaurant:
        <select name="restaurantId" onChange={handleInputChange}>
          <option value="">--Please choose a restaurant--</option>
          <option value="1">Vinland</option>
          <option value="2">Demon Slayer</option>
          <option value="3">Miror</option>
        </select>
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default ContactForm;
